/* global VK */
import React from 'react';
import PropTypes from 'prop-types';
import Panel from '@vkontakte/vkui/dist/components/Panel/Panel';
import PanelHeader from '@vkontakte/vkui/dist/components/PanelHeader/PanelHeader';
import Button from '@vkontakte/vkui/dist/components/Button/Button';
import Group from '@vkontakte/vkui/dist/components/Group/Group';
import Cell from '@vkontakte/vkui/dist/components/Cell/Cell';
import Div from '@vkontakte/vkui/dist/components/Div/Div';
import Avatar from '@vkontakte/vkui/dist/components/Avatar/Avatar';
import bridge from '@vkontakte/vk-bridge';

const Home = ({ id, go, fetchedUser,werter }) => (
	<Panel id={id}>
		<PanelHeader>Example</PanelHeader>
		{fetchedUser &&
		<Group title="User Data Fetched with VK Bridge">
			<Cell
				before={fetchedUser.photo_200 ? <Avatar src={fetchedUser.photo_200}/> : null}
				description={fetchedUser.city && fetchedUser.city.title ? fetchedUser.city.title : ''}
			>
				{`${fetchedUser.first_name} ${fetchedUser.last_name}`}
			</Cell>
			<Div>Добро пожаловать!</Div>
		</Group>}
		<Group title="Navigation Example">
			<Div>
				<Button onClick={function werter() {
	bridge.send("VKWebAppShowNativeAds", {ad_format:"reward"})
.then(data => console.log(data.result))
.catch(error => console.log(error));
}}>
					БОНУС 5x 
				</Button>
			</Div>
		</Group>






	</Panel>
);

Home.propTypes = {
	id: PropTypes.string.isRequired,
	go: PropTypes.func.isRequired,
	fetchedUser: PropTypes.shape({
		photo_200: PropTypes.string,
		first_name: PropTypes.string,
		last_name: PropTypes.string,
		city: PropTypes.shape({
			title: PropTypes.string,
		}),
	}),
};

export default Home;
'use strict';

        let obj = {
            background: '#fd79a8',
            height: '100px',
            width: '300px',
            margin: '50px',
            borderRadius: '20px',
            borderStyle: 'solid',
            borderWidth: '10px',
            borderColor: '#81ecec',
            color: '#ffffff',
            textAlign: '50px',
            fontSize: '50px'

        }
        let body = document.querySelector("body");
        let elem = document.createElement("div");
            elem.style.background = obj.background;
            elem.style.height = obj.height;
            elem.style.width = obj.width;
            elem.style.margin = obj.margin;
            elem.style.borderRadius = obj.borderRadius;
            elem.style.borderStyle = obj.borderStyle;
            elem.style.borderWidth = obj.borderWidth;
            elem.style.borderColor = obj.borderColor;
            elem.style.color = obj.color;
            elem.style.textAlign = obj.textAlign;
            elem.style.fontSize = obj.fontSize;


            body.appendChild(elem);
        let l = 0;
        elem.addEventListener('click', function(){
            l+=1;
            elem.innerHTML = l;
        });
